package com.capgemini.day2;

public class MyOperator {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		// Using the Arithmatic Operators and Assignment operator
		int num1 = 100;
		int num2 = 200;
		
		//Addition
		int a =num1 + num2;
		System.out.println(a);
		
		//Subtraction
		a =num2 - num1;
		System.out.println(a);
		
		//Multiplication
		a =num1 * num2;
		System.out.println(a);
		
		//Division
		a =num2 / num1;
		System.out.println(a);
		
		//Modulus
		a =num2 % num1;
		System.out.println(a);
	}

}
